                setPic(myJson.user[0].pic)
            }
        })
        return name;
    }

    function getlikes(e , i){
        const link1 = "http://127.0.0.1:3000/vote_get_postemail_postid/" + e + "/" + i;
        console.log(link1);
        fetch(link1)
        .then(function(response){
